import react, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) { // construtor, para armazenar o estado ou dados 
    super(props);

    this.state = { // definir as propriedades que serão armazenadas
      showImage: false,
    };
  }

  toggle = () => {
    this.setState({// sempre que for atualizar um state, usa-se o this.setState, em classes
      showImage: !this.state.showImage,
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <button type="button" onClick={this.toggle}>
            Mostrar
            </button>
          {this.state.showImage && ( // renderizar, mostrar a imagem, baseada em uma função
            <img src={logo} className="App-logo" alt="logo" />
          )}

          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
